mapper.select:
select 
from wechat_mp_subscribe_message_template


        and id = #{id}


        and id in (

            #{item}

        )


        and app_name = #{app}


        and template_id like #{templateId}


        and title like "%"#{title}"%"


        and type like "%"#{type}"%"


        and description like "%"#{description}"%"


        and status = #{status}

select count(*)
from wechat_mp_subscribe_message_template
where template_id = #{templateId}
