mapper.update:
update organization
set number_of_members = number_of_members + 1
where id = #{id}

update organization
set number_of_members = number_of_members - 1
where id = #{id}

update organization
set number_of_departments = number_of_departments + 1
where id = #{id}

update organization
set number_of_departments = number_of_departments - 1
where id = #{id}
