mapper.select:
select interview_id
from interview_form
where id = #{id}

select interview_status
from interview_form
where id = #{id}

select count(*)
from interview_form
where interview_id = #{interviewId}
and application_form_id = #{applicationFormId}

mapper.update:
update interview_form
set interview_time = #{interviewTime}
where id = #{id}

update interview_form
set interview_location = #{interviewLocation}
where id = #{id}

update interview_form
set note = #{note}
where id = #{id}

update interview_form
set interview_status = #{newInterviewStatus}
where id = #{id}
and interview_status = #{oldInterviewStatus}
