mapper.select:
select
from role_permission rp inner join permission p on rp.permission_id = p.id where rp.role_id in
#{item}

select

from permission


        and id = #{id}


        and id in (

            #{item}

        )


        and parent_permission_id = #{parentPermissionId}


        and permission_name like "%"#{permissionName}"%"


        and authorization_url like "%"#{authorizationUrl}"%"


        and is_available = #{available}

select id
from permission
where parent_permission_id = #{parentPermissionId}

select id
from permission
where parent_permission_id = #{parentPermissionId}
  and is_available = #{available}

select count(*)
from permission
where id = #{id}

select count(*)
from permission
where permission_name = #{permissionName}

select count(*)
from permission
where id = #{id}
  and is_available = #{available}

select count(*)
from permission
where parent_permission_id = #{parentPermissionId}

mapper.update:
update permission
set permission_name = #{permissionName}
where id = #{id}

update permission
set authorization_url = #{authorizationUrl}
where id = #{id}

update permission
set description = #{description}
where id = #{id}

update permission
set is_available = #{available}
where id = #{id}

update permission
set parent_permission_id = #{parentPermissionId}
where id = #{id}
